mapper.sql:
SELECT l1.id ,l1.type ,( SELECT name FROM common_code WHERE code_group = 'SYSNT_TYPE' AND code = l1.type ) type_name ,l1.title ,l1.content ,l1.status ,if( l1.status = '1', '게시','미게시' ) status_name ,l1.view_cnt ,l1.view_service ,( SELECT group_concat(name separator ' ') FROM common_code WHERE code_group = 'VIEW_SERV' AND find_in_set(code, l1.view_service) != 0 ) view_service_text ,l1.reg_id ,l1.reg_date ,l1.mod_id ,l1.mod_date ,l2.full_name reg_name FROM system_notice l1 LEFT OUTER JOIN user l2 ON l1.reg_id = l2.id
AND DATE_FORMAT(l1.reg_date, '%Y-%m-%d') BETWEEN #{startDate} AND #{endDate}
AND l1.type = #{type}
AND l1.${searchItem} LIKE CONCAT('%',#{searchKeyWord},'%')
AND l1.status = #{status}
AND ( l1.title LIKE CONCAT('%',#{userSearchKeyWord},'%') OR l1.content LIKE CONCAT('%',#{userSearchKeyWord},'%') )
AND l1.title LIKE CONCAT('%',#{userSearchKeyWord},'%')
AND l1.content LIKE CONCAT('%',#{userSearchKeyWord},'%')
find_in_set(#{item},l1.view_service) != 0
AND l1.id = #{id}
ORDER BY reg_date DESC
